mapper.update:
update category
name = #{name}, sort = #{sort}, type = #{type}, update_time = #{updateTime}, update_user = #{updateUser},
where id = #{id}

mapper.select:
select * from category

    name like concat('%',#{name},'%'),
    type = #{type}

select * from category where status = 1
 and type = #{type} 
order by sort asc,create_time desc
